import React, { Component } from 'react';

import Aux from '../../hoc/Aux';

const $ = window.$;


class HeadNav extends Component {
    constructor(props) {
        super(props);
        this.state = { parent: "" };
    }

    openSubmenu() {
        $(".dropdown").addClass("open");
    }
    closeSubmenu() {
        $(".dropdown").removeClass("open");
    }
    static getDerivedStateFromProps(props, state) {
        // this.state.parent = document.getElementById('megamenu12');
        // this.state.parent.onmouseover = this.state.parent.onmouseout = this.state.parent.onmousemove = this.toggleDropdown;
        // this.setState(...this.state.parent);
        var timer;
        $("#megamenu12").on("mouseover", function () {
            clearTimeout(timer);
            openSubmenu();
        }).on("mouseleave", function () {
            timer = setTimeout(
                closeSubmenu
                , 1000);
        });
    }

    toggleDropdown = (e) => {
        console.log("ANil Reddy")
        const _d = $(e.target).closest('.dropdown'),
            _m = $('.dropdown-menu', _d);
        setTimeout(function () {
            const shouldOpen = e.type !== 'click' && _d.is(':hover');
            _m.toggleClass('show', shouldOpen);
            _d.toggleClass('show', shouldOpen);
            $('[data-toggle="dropdown"]', _d).attr('aria-expanded', shouldOpen);
        }, e.type === 'mouseleave' ? 300 : 0);
    }

    //  function handler(event) {
    //     let type = event.type;
    //     while (type < 11) type += ' ';

    //     log(type + " target=" + event.target.id)
    //     return false;
    //   }
